import React, { Component } from 'react';
import { Route, Switch, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

import Searchbar from '../components/Searchbar';
import TripList from '../components/TripList';
import TripDetail from '../components/TripDetail';
import NewTripForm from '../components/NewTripForm'

import { fetchTrips } from '../redux/actions';

class TripContainer extends Component {

  componentDidMount() {
    this.props.fetchTrips();
  }

  render() {
    return (
      <div>
        <Switch>

          <Route path="/" render={() => (
              <div className='ui narrow container segment'>
                <Searchbar />
                <TripList />

                  <button class='positive ui button'>
                  <i class='plus circle icon'></i>
                  Create Trip
                  </button>
                  <br></br>
                  <br></br>

              </div>
            )}
          />

          <Route path="/trips/:tripId" render={data => {
            return (
              <TripDetail tripId={data.match.params.tripId} />
            );
          }}
          />
        </Switch>
      </div>
    )
  }
}

export default withRouter(connect (null, { fetchTrips }, null, {pure:false})(TripContainer));
