import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

class LuggageList extends Component {
  state = {
    numberOfItems: 0,
    luggage: this.props.trip.luggage
  }


  handleItemChange = e => {
    let newItems = this.state.luggage;
    newItems[e.target.id] = e.target.value
    this.setState({
      luggage: newItems
    });
  };

  increaseItems = () => {
    this.setState({
      numberOfItems: this.state.numberOfItems + 1
    });
  };

  renderItemForms = () => {
    let itemForms = [];
    for (let i=0; i < this.state.numberOfItems; i++) {
      itemForms.push(
        <div className='field' key={i}>
          <input id={i} type='text' placeholder='Insert Item' onChange={this.handleItemChange} />
        </div>
      );
    }
    return itemForms;
  };

  render() {
    return (
      <div>
      LuggageList
      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(LuggageList));
