import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import LuggageForm from './LuggageForm'

class LuggageList extends Component {

  render() {
    const trip = this.props.trip;

    if (trip.luggage.length === 0) {
      return (
        <LuggageForm />
      )
    } else {
        return (
          <div className=''>
            {trip.luggage.map(item => (
              <li>{ item }</li>
            ))}
          </div>
        )
      }
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(LuggageList));
