import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { updateLuggageItems } from '../redux/actions';


class LuggageForm extends Component {
  state = {
    luggage: this.state
  };

  handleOnSubmit = e => {
    e.preventDefault();
    let payload = {
      luggage: this.props.trip.luggage
    };
    this.props.updateLuggageItems({
      tripId: this.props.trip.id,
      payload
    });
    this.props.history.push("/trips/" + this.props.trip.id + "/luggage");
  };

  handleOnChange = (e) => {
    e.persist();
    this.setState({
      [e.target.id]: e.target.value
    })
  }

  render() {
    const trip = this.props.trip
    return (
      <form className='ui form'onSubmit={this.handleOnSubmit}>
        <div>
          <input
            className='ui field'
            type='text'
            placeholder='Add Luggage Item'
            name='luggage'
            value={this.state.location}
            onChange={this.handleOnChange}
          />
        </div>
      </form>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, { updateLuggageItems })(LuggageForm));












