import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { updateLuggageItems } from '../redux/actions';


class LuggageForm extends Component {
  state = {
    itemCount: "",
    item: ""
  };

  handleOnSubmit = e => {
    e.preventDefault();
    let payload = {
      luggage: this.props.trip.luggage
    };
    this.props.updateLuggageItems({
      tripId: this.props.trip.id,
      payload
    });
    this.props.history.push("/trips/" + this.props.trip.id + "/luggage");
  };

  handleItemChange = (e) => {
    e.persist();
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleQtyChange = (e) => {
    e.persist();
    this.setState({
      [e.target.id]: e.target.value
    })
  }

  render() {
    const trip = this.props.trip
    return (
      <form className='ui form' onSubmit={this.handleOnSubmit}>
        <div className='ui horizontal fields'>
          <div className='ui field'>
            <select className='ui fluid dropdown'>
              <option value="">Qty.</option>
              <option id='itemCount' value="1" onChange={this.handleQtyChange}>1</option>
              <option id='itemCount' value="2" onChange={this.handleQtyChange}>2</option>
              <option id='itemCount' value="3" onChange={this.handleQtyChange}>3</option>
              <option id='itemCount' value="4" onChange={this.handleQtyChange}>4</option>
              <option id='itemCount' value="5" onChange={this.handleQtyChange}>5</option>
            </select>
          </div>
          <div className='ui field'>
            <input
            className='ui field'
            type='text'
            placeholder='Add Luggage Item'
            name='item'
            value={this.state.item}
            onChange={this.handleOnChange}
            />
          </div>
        </div>
      </form>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, { updateLuggageItems })(LuggageForm));












