import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

class Luggage extends Component {
  state = {
    numberOfItems: 0,
    luggage: this.props.trip.luggage
  }

  handleOnSubmit = e => {
    e.preventDefault();
    let payload = {
      luggage: this.state.luggage
    };
    this.props.updateLuggageItems({
      tripId: this.props.trip.id,
      payload
    });
    this.props.history.push("/trips/" + this.props.trip.id + "/luggage");
  };

  increaseItems = () => {
    this.setState({
      numberOfItems: this.state.numberOfItems + 1
    });
  };

  render() {
    const trip = this.props.trip
    return (
      <form className='ui form'onSubmit={this.handleOnSubmit}>
        <div className='field'>
          <input type='text' placeholder='Add Luggage Item' onChange={this.handleItemChange}/>

          <button className='ui left floated green basic button' onClick={this.increaseItems}>
          <i className='plus circle icon'></i>
          Add Item
          </button>
        </div>
      </form>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(Luggage));
