import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import LuggageForm from './LuggageForm'

class LuggageList extends Component {

  render() {
    const trip = this.props.trip;
    // console.log(trip.luggages)
    return (
      <div className='ui celled list'>
        {trip.luggages.map(luggage => (
          <div className='item'>
            <div className='content'>{luggage.qty} - {luggage.item}</div>
            <Link to={`/trips/${this.props.trip.id}/luggages`}>
              <button className='ui floated right button'
                onClick={this.handleOnDelete}>
                <i className='ui red remove icon'/>
              </button>
            </Link>
          </div>
        ))}
      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(LuggageList));
