import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

class Luggage extends Component {
  state = {
    numberOfItems: this.props.trip.luggage.length,
    luggage: this.props.trip.luggage
  }



  render() {
    const trip = this.props.trip
    return (
      <div className='ui form'>
        <div className='field'>
          <div className='ui checkbox'>
            <input type='checkbox' tabIndex="0" className='hidden'/>
            <label>Add Luggage Item</label>
          </div>
        </div>
        <div className='field'>
        <button className='ui left floated green basic button'>
        <i className='plus circle icon'></i>
        Add Item
        </button>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(Luggage));
