import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import LuggageForm from './LuggageForm'

class LuggageList extends Component {

  handleOnDelete = e => {
    e.preventDefault();
    const itemId = e.id;
    const tripId = parseInt(this.props.tripId)

    this.props.deleteLuggageItem(itemId, tripId);
    this.props.history.push("/trips/" + tripId + "/luggages");
  }
  render() {
    const trip = this.props.trip;
    // console.log(trip.luggages)
    return (
      <div className='ui middle aligned divided list'>
        {trip.luggages.map(luggage => (
          <div id={luggage.id} className='item'>
            <NavLink to={`/trips/${this.props.trip.id}/luggages`}>
              <button className='ui right floated icon button'>
                <i className='red remove icon' />
              </button>
            </NavLink>
            <div className='content'>
              {luggage.qty} - {luggage.item}
            </div>
          </div>
        ))}
      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(LuggageList));
