import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

class Luggage extends Component {
  state = {
    numberOfItems: 0,
    luggage: this.props.trip.luggage
  }

  handleItemChange = e => {
    let newItems = this.state.luggage;
    newItems[e.target.id] = e.target.value
    this.setState({
      luggage: newItems
    });
  };

  increaseItems = () => {
    this.setState({
      numberOfItems: this.state.numberOfItems + 1
    });
  };

  renderItemForms = () => {
    let itemForms = [];
    for (let i=0; i < this.state.numberOfItems; i++) {
      itemForms.push(
        <div className='field' key={i}>
          <input id={i} type='text' placeholder='Insert Item' onChange={this.handleItemChange} />
        </div>
      );
    }
    return itemForms;
  };

  render() {
    const trip = this.props.trip
    return (
      <form className='ui form'>
        <div className='field'>
          <div>
            <input type='text' placeholder='Add Luggage Item' onChange={this.handleItemChange}/>
          </div>
        </div>
        {this.renderItemForms()}
        <button className='ui left floated green basic button' onClick={this.increaseItems}>
          <i className='plus circle icon'></i>
          Add Item
        </button>
      </form>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(Luggage));
