import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { updateLuggageItems } from '../redux/actions';


class LuggageForm extends Component {
  state = {
    item: ""
  };

  handleOnSubmit = e => {
    const newItem = this.state.itemCount
    const luggageArray = [
      ...this.props.trip.luggage,
      newItem
    ]

    e.preventDefault();
    let payload = {
      luggage: luggageArray
    };
    this.props.updateLuggageItems({
      tripId: this.props.trip.id,
      payload
    });
    this.props.history.push("/trips/" + this.props.trip.id);
  };

  handleOnChange = (e) => {
    e.persist();
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    return (
      <form className='ui form' onSubmit={this.handleOnSubmit}>
        <div className='ui horizontal fields'>
          <div className='ui field'>
            <input
            className='ui field'
            type='text'
            placeholder='Add Luggage Item'
            name='item'
            value={this.state.item}
            onChange={this.handleOnChange}
            />
          </div>
        </div>
      </form>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, { updateLuggageItems })(LuggageForm));










