import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';

import Searchbar from '../components/Searchbar';
import TripList from '../components/TripList';
import TripDetail from '../components/TripDetail'

import { fetchTrips } from '../redux/actions';

class TripContainer extends Component {

  componentDidMount() {
    this.props.fetchTrips();
  }

  render() {
    return (
      <div>
        <Switch>
          <Route path="/" render={() => (
              <div className='ui narrow container segment'>
                <Searchbar />
                <TripList />
                <br></br>
                <button class='circular ui icon button'>
                  <i class='plus circle icon'></i>
                </button>
              </div>
            )}
          />
          <Route path="/trips/:tripId" render={data => {
            return (
              <TripDetail tripId={data.match.params.tripId} />
            );
          }}
          />
        </Switch>
      </div>
    )
  }
}

export default connect (null, { fetchTrips })(TripContainer);
