import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { updateTrip } from '../redux/actions';

import TripDetailContainer from '../containers/TripDetailContainer';

class TripDetailForm extends Component {
  state = {};

  static getDerivedStateFromProps(props, state) {
    if (props.trip && props.trip.id !== state.id) {
      return {
        id: props.trip.id,
        location: props.trip.location,
        startDate: props.trip.startDate,
        endDate: props.trip.endDate,
        notes: props.trip.notes
      };
    }
    return null;
  }

  onSave = e => {
    e.preventDefault();
    let payload = {
      location: this.state.location,
      startDate: this.state.startDate,
      endDate: this.state.endDate,
      notes: this.state.notes
    };
    this.props.updateTrip({
      tripId: this.props.trip.id,
      payload
    });
    this.props.history.push("/trips/" + this.props.trip.id);
  };

  render() {
    if (!this.props.trip) {
      return (
        <div className='ui segment'>
          <div className='ui active dimmer'>
            <div className='ui text loader'>
            Loading Trip...
            </div>
          </div>
        </div>
      );
    }
    return (
      <div>
      <form className='ui form' onSubmit={this.onSave}>
        <div>
          <label>Location:</label>
          <input className='ui field' type='text' name='location' value={this.state.location} onChange={this.handleOnChange} />
        </div>
        <div>
          <label>From:</label>
          <input className='ui field' type='text' placeholder='DD-MM-YYYY' id='startDate' value={this.state.startDateConverted} onChange={this.handleOnChange} />
          <label>To:</label>
          <input className='ui field' type='text' placeholder='DD-MM-YYYY' id='endDate' value={this.state.endDateConverted} onChange={this.handleOnChange} />
        </div>
        <div>
          <label>Notes:</label>
          <input className='ui field' type='text' id='notes' value={this.state.notes} onChange={this.handleOnChange} />
        </div>
        <Link to={'/trips'}>
          <button className='ui button' type='button'>Cancel</button>
        </Link>
        <button className='ui button' type='submit'>Submit</button>
      </form>
        <h3>{this.props.trip.location}</h3>
        <h3>Dates</h3>
        <li>{this.props.trip.startDateConverted} - {this.props.trip.endDateConverted}</li>
        <h3>Notes</h3>
        <li>{this.props.trip.notes}</li>

        <TripDetailContainer />

        <Link to={`/trips/${this.props.trip.id}/edit`}>
          <button className='ui button' type='button'>Edit</button>
        </Link>


      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(TripDetailForm));
