import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

import TripDetailContainer from '../containers/TripDetailContainer';

class TripDetail extends Component {
  render() {
    if (!this.props.trip) {
      return (
        <div className='ui segment'>
          <div className='ui active dimmer'>
            <div className='ui text loader'>
            Loading Trip...
            </div>
          </div>
        </div>
      );
    }
    return (
      <div>
        <div>
          <h3>Location:</h3>
          <h3>{this.props.trip.location}</h3>
        </div>
        <div>
          <h3>From:</h3>
          <h3>{this.props.trip.startDate}</h3>

          <label>To:</label>
          <input
            className='ui field'
            type='text'
            name='endDate'
            value={this.state.endDateConverted}
            onChange={this.setState({ endDate: e.target.value })}
          />
        </div>
        <div>
          <label>Notes:</label>
          <input
          className='ui field'
          type='text'
          name='notes'
          value={this.state.notes}
          onChange={this.setState({ notes: e.target.value })}
          />
        </div>
        <Link to={'/trips'}>
          <button className='ui button' type='button'>Cancel</button>
        </Link>
        <button className='ui button' type='submit'>Submit</button>

      <TripDetailContainer />

      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(TripDetail));
