import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import FlightInfoForm from './FlightInfoForm'
import { fetchSpecificTripFlightInfoList } from '../redux/actions';


class FlightInfoList extends Component {
  state = {};

  // handleOnDelete = (itemId) => {
  //   const tripId = this.props.specificTrip.id
  //   console.log("itemId:", itemId)
  //   console.log("tripId:", tripId)
  //
  //   this.props.deleteLuggageItem(itemId, tripId);
  //   this.props.history.push(`/trips/${tripId}/luggage`);
  // }

  render() {
    if (!this.props.specificTrip.flights) {
      return (
        <div className='ui segment'>
          <div className='ui active dimmer'>
            <div className='ui text loader'>
              Loading Flights List...
            </div>
          </div>
        </div>
      )
    }

    const trip = this.props.specificTrip;
    return (
      <div className='ui middle aligned divided list'>
        {trip.flights.map(flight => (
          <div id={flight.id} className='ui inverted blue card'>
            <div className='content'>
            <h4 className='ui blue dividing header'></h4>
            <div className='ui horizontal segments'>
              <div className='ui segment'>From:
                <h5>{flight.from}</h5>
              </div>
              <div className='ui segment'>To:
                <h5>{flight.to}</h5>
              </div>
            </div>
            <div className='field'>
              <div className='two fields'>
                <div className='field'>
                  <label>Flight Number</label>
                  <input type='text' placeholder='What is your flight number?'/>
                </div>
                <div className='field'>
                  <label>Departure</label>
                  <select class='ui search dropdown'>
                    <option value="">Time</option>
                  </select>
                </div>
              </div>
            </div>
            <div className='field'>
              <div className='three fields'>
                <div className='five wide field'>
                  <label>Gate</label>
                  <input type='text'/>
                </div>
                <div className='five wide field'>
                  <label>Boarding Group</label>
                  <input type='text' maxlength='3'/>
                </div>
                <div className='five wide field'>
                  <label>Seat</label>
                  <input type='text' maxlength='3'/>
                </div>
              </div>
            </div>
              From: {flight.from}
              To: {flight.to}
              Flight Number: {flight.flightNumber}
              Departure: {flight.departure}
              Gate: {flight.gate}
              Boarding Group: {flight.boardingGroup}
              Seat: {flight.seat}
            </div>
          </div>
        ))}
      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  // console.log(state)
  return {
    specificTrip: state.specificTrip
  }
}

export default withRouter(connect(mapStateToProps, { fetchSpecificTripFlightInfoList })(FlightInfoList));
