import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { updateTrip } from '../redux/actions';

import TripDetailContainer from '../containers/TripDetailContainer';

class TripDetailForm extends Component {
  state = {};

  render() {
    if (!this.props.trip) {
      return (
        <div className='ui segment'>
          <div className='ui active dimmer'>
            <div className='ui text loader'>
            Loading Trip...
            </div>
          </div>
        </div>
      );
    }
    return (
      <div>
        <h3>{this.props.trip.location}</h3>
        <h3>Dates</h3>
        <li>{this.props.trip.startDateConverted} - {this.props.trip.endDateConverted}</li>
        <h3>Notes</h3>
        <li>{this.props.trip.notes}</li>

        <TripDetailContainer />

        <Link to={`/trips/${this.props.trip.id}/edit`}>
          <button className='ui button' type='button'>Edit</button>
        </Link>


      </div>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(TripDetailForm));
