import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';

class LuggageForm extends Component {
  state = {
    numberOfItems: 0
  }

  handleOnSubmit = e => {
    e.preventDefault();
    let payload = {
      luggage: this.state.luggage
    };
    this.props.updateLuggageItems({
      tripId: this.props.trip.id,
      payload
    });
    this.props.history.push("/trips/" + this.props.trip.id + "/luggage");
  };


  render() {
    const trip = this.props.trip
    return (
      <form className='ui form'onSubmit={this.handleOnSubmit}>
        <div className='field'>
          <input type='text' placeholder='Add Luggage Item' onChange={this.handleItemChange}/>
        </div>
      </form>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  console.log(state)
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, null)(LuggageForm));













