import React, { Component } from 'react';
import { NavLink, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { updateLuggageItems } from '../redux/actions';


class LuggageForm extends Component {
  state = {
    qty: null,
    item: ""
  };

  handleOnSubmit = e => {
    const newItem = this.state.item;
    const luggageArray = [
      ...this.props.trip.luggage,
      newItem
    ];

    e.preventDefault();
    let payload = {
      luggageArray: luggageArray
    };
    this.props.updateLuggageItems({
      payload,
      tripId: this.props.trip.id
    });
    this.props.history.push("/trips/" + this.props.trip.id + "/luggage");
  };

  handleOnChange = (e) => {
    e.persist();
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render() {
    return (
      <form className='ui form' onSubmit={this.handleOnSubmit}>
        <div className='ui horizontal fields'>
          <div className='ui field'>
            <select className='ui fluid dropdown' onChange={e => this.setState({ qty: parseInt(e.target.value) })}>
              <option value="">Qty.</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>
          <div className='ui field'>
            <input
            className='ui field'
            type='text'
            placeholder='Add Luggage Item'
            name='item'
            value={this.state.item}
            onChange={e => this.setState({ item: e.target.value })}
            />
          </div>
        </div>
      </form>
    )
  }
}

const mapStateToProps = (state, props) => {
  let trip = state.trips.find(t => t.id === Number(props.tripId))
  return {
    trip: trip
  }
}

export default withRouter(connect(mapStateToProps, { updateLuggageItems })(LuggageForm));












