import { combineReducers } from 'redux';


const searchTextReducer = (state = "", action) => {
  switch (action.type) {
    case "CHANGE_SEARCH_TEXT":
      return action.value;
    default:
      return state
  }
}

const specificTripReducer = (state = null, action) => {
  switch (action.type) {
    case "LUGGAGE_ITEM_ADDED":
      return state.luggages.map(trip => {
        if (trip.id === action.trip_id) {
          return [...state.luggages, action.newItem];
        } else {
          return trip
        }
      });
    case "LUGGAGE_ITEM_DELETED":
      console.log("state:", state)
      return state.filter(item => item.id !== action.item.id);
    case "UPDATE_LUGGAGE":
      return [...state.luggages, action.item];
    case "FETCHED_SPECIFIC_TRIP":
      return action.trip;
    case "FETCHED_SPECIFIC_TRIP_LUGGAGE_LIST":
      return action.tripLuggageList;
    default:
      return state;
  }
}

const tripsReducer = (state = [], action) => {
  switch (action.type) {
    case "TRIP_ADDED":
      return [...state, action.trip];
    case "TRIP_UPDATED":
      return state.map(trip => {
        if (trip.id === action.trip.id) {
          return action.trip;
        } else {
          return trip;
        }
      });
    case "FETCHED_TRIPS":
      return action.trips;
    default:
      return state;
  }
}

const loadingReducer = (state = false, action) => {
  switch (action.type) {
    case "FETCHING_TRIPS":
      return true;
    case "FETCHED_TRIPS":
      return false;
    case "FETCHING_SPECIFIC_TRIP":
      return true;
    case "FETCHED_SPECIFIC_TRIP":
      return false;
    case "FETCHING_SPECIFIC_TRIP_LUGGAGE_LIST":
      return true;
    case "FETCHED_SPECIFIC_TRIP_LUGGAGE_LIST":
      return false;
    default:
      return state;
  }
};

const rootReducer = combineReducers({
  searchText: searchTextReducer,
  trips: tripsReducer,
  loading: loadingReducer,
  specificTrip: specificTripReducer
})

export default rootReducer;
